// Determine response type
let responseType = 'default';
const messageLower = message.toLowerCase();

if (messageLower.includes('hello') || messageLower.includes('hi') || messageLower.includes('greetings')) {
  responseType = 'greeting';
} else if (messageLower.includes('security') || messageLower.includes('secure') || messageLower.includes('vulnerability')) {
  responseType = 'security';
} else if (messageLower.includes('war') || messageLower.includes('battle') || messageLower.includes('attack')) {
  responseType = 'war';
} else if (messageLower.includes('status') || messageLower.includes('report') || messageLower.includes('check')) {
  responseType = 'status';
}

// Get random response
const responses = apexResponses[responseType];
const randomResponse = responses[Math.floor(Math.random() * responses.length)];

// Return APEX response
return {
  message: randomResponse,
  channel: channel,
  user: user,
  responseType: responseType,
  timestamp: new Date().toISOString()
};
